        [RelayCommand]
        public async Task Select(ChoiceItem item)
        {
            try
            {
                var questionItem = CurrentQuestion.Questions.Where(x => x.Choices.Any(c => c == item)).FirstOrDefault();
                var choiceList = questionItem.Choices;
                //如果是已经显示了答案   就不允许再修改了
                if (questionItem.IsShowAnswer)
                    return;
                if (CurrentQuestion.QuestionType == QuestionType.Single||CurrentQuestion.QuestionType == QuestionType.Judge)
                {
                    foreach (var choice in choiceList)
                    {
                        if (item == choice)
                        {
                            choice.IsChecked = !choice.IsChecked;
                            if(IsPracticeMode)
                                questionItem.IsShowAnswer = true;
                            if (choice.IsChecked)
                                questionItem.MyAnswer = choice.Title;
                            continue;
                        }
                        choice.IsChecked = false;
                    }
                }
                else if(CurrentQuestion.QuestionType == QuestionType.Multiple||CurrentQuestion.QuestionType == QuestionType.Indefinite)
                {
                    item.IsChecked = !item.IsChecked;
                    var answer = string.Empty;
                    foreach(var c in questionItem!.Choices!)
                    {
                        if (c.IsChecked)
                            answer += c.Title;
                    }
                    questionItem.MyAnswer = answer;
                    if (!string.IsNullOrEmpty(questionItem.MyAnswer))
                    {
                        if(CurrentQuestion.QuestionType == QuestionType.Multiple&&questionItem.MyAnswer.Length < 2 )
                        {
                            questionItem.IsChoiced = false;
                        }
                        else
                        {
                            questionItem.IsChoiced = true;
                        }
                    }
                    else
                    {
                        questionItem.IsChoiced= false;
                    }
                }
            }
            catch( Exception ex)
            {
                await Shell.Current.DisplayAlert("异常", $"{ex.ToString()}", "OK");
            }
        }
